</template>

<script>

export default {
    name: "Blog-Page",
    watch: {
        $route: {
            immediate: true,
            handler(to, from) {
                document.title = to.meta.title || "Blog - Women's Diary";
            }
        }
    },

    data() {
        return {
            articles: []
        };
    },
    mounted() {
        fetch('http://localhost:8000/api/article')
            .then(response => response.json())
            .then(data => {
                this.articles = data;
            })
            .catch(error => console.log(error));
        console.log(this.articles);
    }
};

</script>

